modelTx = {"cid": 0, "round": 1, "model": ""}
modelTx = json.dumps(modelTx)
modelB64Str = base64.b64encode(modelTx.encode('utf-8')).decode('utf-8')
payload = {
    "method": "broadcast_tx_sync",
    "params": [modelB64Str],
    "jsonrpc": "2.0",
    "id": 1,
}
response = requests.post(url, json=payload).json()
print(response)